import * as types from "./../constants/ActionsType"

var initialState = [
    {
        product: {
            id: 1,
            name: "Iphone 8x",
            image: "https://cdn.cellphones.com.vn/media/catalog/product/cache/7/small_image/300x/9df78eab33525d08d6e5fb8d27136e95/i/p/iphone_8_64gb.jpg",
            description: "San Pham cua Apple",
            price: 300,
            inventory: 10,
            rating: 5
        },
        quantity: 4
    },
    {
        product: {
            id: 2,
            name: "Samsum Galaxy",
            image: "https://clickbuy.com.vn/uploads/2019/04/samsung-galaxy-s8-do.png",
            description: "San Pham cua SamSum",
            price: 250,
            inventory: 12,
            rating: 4
        },
        quantity: 4
    }
];

const cart = (state = initialState, action) => {
    var {product, quantity} = action;
    switch (action.type) {
        case types.ADD_TO_CART:
            var index = -1;
            index = findProduct(state, product);
            if(index !== -1){
                state[index].product.quantity += product.quantity;
                console.log(state[index].product.quantity);
            }else{
                state.push({
                    product,
                    quantity
                });
            }
            return [...state];
        default: return [...state];
    }
}

var findProduct = (cart, product) =>{
    var index = -1;
    if(cart.length > 0){
        for(var i=0; i< cart.length; i++){
            if(cart[i].product.id === product.id){
                index = i;
                break;
            }
        }
    }
    return index;
}

export default cart;
