import React, { Component } from 'react';

class Product extends Component {
  render() {
    var { product } = this.props;
    return (
      <div className="col-lg-4 col-md-6 mb-r">
        <div className="card text-center card-cascade narrower">
          <div className="view overlay hm-white-slight z-depth-1">
            <img src={product.image}
              className="img-fluid" alt={product.name} />
            <a href="#" title="">
              <div className="mask waves-light waves-effect waves-light"></div>
            </a>
          </div>
          <div className="card-body">
            <h4 className="card-title">
              <strong>
                <a href="#" title="">{product.name}</a>
              </strong>
            </h4>
            <ul className="rating">
              {this.showRating(product.rating)}
            </ul>
            <p className="card-text">
              {product.description}
            </p>
            <div className="card-footer">
              <span className="left">{product.price}$</span>
              <span className="right">
                <button className="btn-floating blue-gradient" data-toggle="tooltip" data-placement="top" data-original-title="Add to Cart" onClick= { () => this.addToCart(product) }>
                  <i className="fa fa-shopping-cart"></i>
                </button>
              </span>
            </div>
          </div>
        </div>
      </div>
    );
  }

  showRating(rating) {
    var result = [];
    for (var i = 1; i <= rating; i++) {
      result.push(<li key={i}><i className="fa fa-star"></i></li>);
    }
    for (var j = 1; j <= (5 - rating); j++) {
      result.push(<li key={j * 10}><i className="fa fa-star-o"></i></li>);
    }
    return result;
  }

  addToCart = (product) =>{
      this.props.onAddToCart(product);
  }

}

export default Product;
